mapper.update:
update order
set
mobile=#{mobile}
,package_id=#{packageId}
,step=#{step}
,current_step=#{currentStep}
,sort=#{sort}
,update_date=now()
where
id=#{id}

mapper.select:
SELECT u.id as id,o.id as orderId ,u.`mobile`,o.sort ,p.package_name as packageName, p.image_url as imageUrl ,p.step ,o.current_step as currentStep FROM `order` o,`user` u,pic_package p WHERE o.mobile=u.mobile AND o.package_id = p.package_id
and o.mobile=#{entity.mobile}
ORDER BY o.sort limit ${page.begin},${page.limit} ;

SELECT o.id as orderId ,o.sort ,p.package_name as packageName, p.image_url as imageUrl FROM `order` o,`user` u,pic_package p WHERE o.mobile=u.mobile AND o.package_id = p.package_id
and o.mobile=#{entity.mobile}
ORDER BY o.sort limit ${page.begin},${page.limit} ;

SELECT u.id as id,o.id as orderId ,u.`mobile`,o.sort ,p.package_name as packageName, p.image_url as imageUrl ,p.step ,o.current_step as currentStep FROM `order` o,`user` u,pic_package p WHERE o.mobile=u.mobile AND o.package_id = p.package_id
and o.mobile=#{mobile}
and o.id=#{orderId}
LIMIT 1
