SELECT u.id as id,o.id as orderId ,u.`mobile`,o.sort ,p.package_name as packageName,p.package_id as packageId, o.template as template, p.image_url as imageUrl ,p.step ,o.current_step as currentStep,p.pins as pins FROM `order` o,`user` u,pic_package p WHERE o.mobile=u.mobile AND o.package_id = p.package_id
and o.mobile=#{mobile}
and o.id=#{id}
limit 1

SELECT u.id as id,o.id as orderId ,u.`mobile`,o.sort ,p.package_name as packageName, p.image_url as imageUrl ,p.min_image_url as minImageUrl ,p.step ,o.current_step as currentStep, o.template as template FROM `order` o,`user` u,pic_package p WHERE o.mobile=u.mobile AND o.package_id = p.package_id
and o.mobile=#{mobile}
and o.id=#{id}
ORDER BY o.sort, o.`update_date` desc

SELECT * FROM `order` o 
WHERE
1 = 1

	and o.mobile=#{mobile}


	and o.id=#{id}

ORDER BY o.sort, o.`update_date` desc

