SELECT u.id AS id,o.id AS orderId ,o.`mobile`,u.`user_name` AS userName,o.sort ,p.package_name AS packageName,p.package_id AS packageId, o.template AS template, p.image_url AS imageUrl ,p.step ,o.current_step AS currentStep,p.pins AS pins FROM `order` o,`user` u,pic_package p WHERE o.user_id=u.id AND o.package_id = p.package_id
and u.user_name=#{userName}
and o.id=#{id}
limit 1

SELECT u.id AS id,o.id AS orderId ,u.`user_name` AS userName,o.sort ,o.mobile,p.package_name AS packageName, p.image_url AS imageUrl ,p.min_image_url AS minImageUrl ,p.step ,o.current_step AS currentStep,o.template AS template FROM `order` o,`user` u,pic_package p WHERE o.user_id=u.id AND o.package_id = p.package_id
and u.user_name=#{userName}
and o.id=#{id}
ORDER BY o.sort, o.`update_date` desc

SELECT * FROM `order` o 
WHERE
1 = 1

	and o.mobile=#{mobile}


	and o.id=#{id}

ORDER BY o.sort, o.`update_date` desc

SELECT u.id AS id,o.id AS orderId ,u.`user_name` AS userName,o.sort ,o.mobile,p.package_name AS packageName, p.image_url AS imageUrl ,p.min_image_url AS minImageUrl ,p.step ,o.current_step AS currentStep,o.template AS template FROM `order` o,`user` u,pic_package p WHERE o.user_id=u.id AND o.package_id = p.package_id
and u.user_name=#{entity.userName}
ORDER BY o.sort, o.`update_date` desc limit ${page.begin},${page.limit} ;

SELECT o.id AS orderId ,o.sort ,p.`come_from` AS comeFrom, p.package_name AS packageName, p.image_url AS imageUrl FROM `order` o,`user` u,pic_package p WHERE o.user_id=u.id AND o.package_id = p.package_id
and o.mobile=#{entity.mobile}
ORDER BY o.sort ,o.`update_date` DESC limit ${page.begin},${page.limit} ;

SELECT u.id AS id,o.id AS orderId ,o.`mobile`,o.sort ,p.package_name AS packageName, p.`come_from` AS comeFrom, p.image_url AS imageUrl ,p.step ,o.current_step AS currentStep,p.pins AS pins FROM `order` o,`user` u,pic_package p WHERE o.user_id=u.id AND o.package_id = p.package_id
and o.mobile=#{mobile}
and o.id=#{orderId}
LIMIT 1

